INSERT INTO `wp_snippets` VALUES (1,'Make upload filenames lowercase','Makes sure that image and file uploads have lowercase filenames.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_filter( \'sanitize_file_name\', \'mb_strtolower\' );','sample, media','global',10,0,'2025-02-10 10:25:03',2,NULL),(2,'Disable admin bar','Turns off the WordPress admin bar for everyone except administrators.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );','sample, admin-bar','front-end',10,0,'2025-02-10 10:25:03',2,NULL),(3,'Allow smilies','Allows smiley conversion in obscure places.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );','sample','global',10,0,'2025-02-10 10:25:03',2,NULL),(4,'Current year','Shortcode for inserting the current year into a post or page..\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','<?php echo date( \'Y\' ); ?>','sample, dates','content',10,0,'2025-02-10 10:25:03',2,NULL),(5,'New blocks','','function wootech_register_blocks() {\n    // Register inline script for custom block\n    wp_register_script(\n        \'wootech-custom-blocks\',\n        \'\',\n        [\'wp-blocks\', \'wp-element\', \'wp-editor\', \'wp-block-editor\'],\n        false,\n        true\n    );\n\n    wp_add_inline_script(\n        \'wootech-custom-blocks\',\n        \"\n        (function() {\n            const { registerBlockType } = wp.blocks;\n            const { useBlockProps, MediaUpload } = wp.blockEditor;\n            const { TextControl, Button } = wp.components;\n            const { createElement } = wp.element;\n\n            registerBlockType(\'wootech/home-banner\', {\n                title: \'Home Banner\',\n                icon: \'cover-image\',\n                category: \'common\',\n                attributes: {\n                    imageUrl: { type: \'string\', default: \'\' },\n                    title: { type: \'string\', default: \'Default Banner Title\' },\n                    description: { type: \'string\', default: \'Short Banner Description\' },\n                },\n                edit: function(props) {\n                    return createElement(\n                        \'div\',\n                        useBlockProps({ className: \'relative w-full h-[330px] md:h-full\' }),\n                        createElement(MediaUpload, {\n                            onSelect: (media) => props.setAttributes({ imageUrl: media.url }),\n                            allowedTypes: [\'image\'],\n                            render: ({ open }) => createElement(\n                                Button,\n                                { onClick: open, isPrimary: true },\n                                props.attributes.imageUrl ? \'Change Image\' : \'Upload Image\'\n                            )\n                        }),\n                        createElement(TextControl, {\n                            label: \'Title\',\n                            value: props.attributes.title,\n                            onChange: (newTitle) => props.setAttributes({ title: newTitle }),\n                        }),\n                        createElement(TextControl, {\n                            label: \'Description\',\n                            value: props.attributes.description,\n                            onChange: (newDesc) => props.setAttributes({ description: newDesc }),\n                        })\n                    );\n                },\n                save: function() {\n                    return null; // Ensures PHP rendering\n                }\n            });\n        })();\n        \"\n    );\n\n    register_block_type(\'wootech/home-banner\', [\n        \'editor_script\' => \'wootech-custom-blocks\',\n        \'render_callback\' => \'wootech_render_home_banner\',\n    ]);\n}\nadd_action(\'init\', \'wootech_register_blocks\');\n\n/**\n * Render the Home Banner Block on the frontend.\n */\nfunction wootech_render_home_banner($attributes) {\n    // Ensure attributes exist\n    $imageUrl = isset($attributes[\'imageUrl\']) && !empty($attributes[\'imageUrl\']) ? esc_url($attributes[\'imageUrl\']) : get_template_directory_uri() . \'/assets/images/home_banner.jpg\';\n    $title = !empty($attributes[\'title\']) ? esc_html($attributes[\'title\']) : \'Default Banner Title\';\n    $description = !empty($attributes[\'description\']) ? esc_html($attributes[\'description\']) : \'Short Banner Description\';\n\n    ob_start();\n    ?>\n    <div class=\"relative w-full\">\n        <!-- Background Image -->\n        <div class=\"absolute inset-0 w-full h-full\">\n            <img class=\"absolute inset-0 w-full h-full object-cover\" src=\"<?php echo $imageUrl; ?>\" alt=\"hero\">\n            <div class=\"absolute inset-0 bg-gradient-to-r from-[rgba(0,0,0,0.35)] md:from-[rgba(0,0,0,0.65)] to-[rgba(0,0,0,0)]\"></div>\n        </div>\n\n        <!-- Overlay Content -->\n        <div class=\"absolute inset-0 flex items-center justify-center text-white\">\n            <div class=\"text-center px-6\">\n                <h1 class=\"text-4xl font-bold\"><?php echo $title; ?></h1>\n                <p class=\"mt-2 text-lg\"><?php echo $description; ?></p>\n            </div>\n        </div>\n    </div>\n    <?php\n    return ob_get_clean();\n}\n\n/**\n * Ensure the Home Banner Block is processed in the_content.\n */\nadd_filter(\'the_content\', function ($content) {\n    global $post;\n    if (has_block(\'wootech/home-banner\', $post)) {\n        // Ensure the block attributes are extracted properly\n        preg_match(\'/<!-- wp:wootech\\/home-banner (.*?) \\/-->/s\', $post->post_content, $matches);\n        \n        $attributes = !empty($matches[1]) ? json_decode($matches[1], true) : [];\n\n        $content .= wootech_render_home_banner($attributes);\n    }\n    return $content;\n});','','global',10,0,'2025-02-14 07:41:49',14,NULL);
